<?phpdefined('PAS_D_ACCES_DIRECT') or die('<h1>Acc&egrave;s Restreint</h1>');$action 	= get ('action', 'accueil');$vue		= 'enregistrement';if (in_array ($action, array ('accueil', 'enregistrer'))){	$action ();}function accueil (){}function enregistrer (){	$nom = post ('identifiant', 'aucun');	$pass = md5 (post ('mot_de_passe', 'aucun'));	if ($membre = existeEtudiant ($nom, $pass))	{		session ('id', $membre->id, true);		session ('type', "etudiant", true);	}	else if ($membre = existeEnseignant ($nom, $pass))	{		session ('id', $membre->id, true);		session ('type', "enseignant", true);	}	else if ($membre = existeAdministrateur ($nom, $pass))	{		session ('id', $membre->id, true);		session ('type', "administrateur", true);	}	else	{		session ('message', 'L&acute;identifiant ou le mot de passe entr&eacute; est incorrect', true);	}		redirrect ('action=accueil');	exit();}?>