<?phpdefined('PAS_D_ACCES_DIRECT') or die('<h1>Acc&egrave;s Restreint</h1>');// Vérification de sécurité// -- Début$idValide = false;$id = session ('id');$etudiant = null;if (!empty ($id)){	if ($etudiant = modeleInfosEtudiant ($id))	{		$idValide = true;	}}if (!$idValide){	session ('type', 'enregistrement', true);	session ('message', 'Veuillez vous enregistrer', true);	redirrect ('action=accueil');}// -- Fin// Traitement de la requêtte$action 	= get ('action', 'accueil');$vueAction		= $vue.'/accueil';$liste		= array ();if (in_array ($action, array ('accueil', 'deconnecter', 'voirnotes'))){	// appel de la fonction qui exécute la requette	$action ();	global $vue, $vueAction;	$vueAction = $vue.'/'.$action;}function accueil (){ }function deconnecter (){	session_destroy();	redirrect ('action=accueil');}function voirnotes (){	global $liste, $id;	$liste = modeleListeNotes ($id);}?>