<?phpdefined('PAS_D_ACCES_DIRECT') or die('<h1>Acc&egrave;s Restreint</h1>');function requette ($sql){	$liste = array ();	$reponse = mysql_query($sql);	if ($reponse)	{		while ($donnee = mysql_fetch_object($reponse) )		{			array_push ($liste, $donnee);		}	}		return $liste;}function existeEtudiant ($nom, $pass){	$sql = 'SELECT * FROM etudiant WHERE identifiant = "'.$nom.'" AND pass = "'.$pass.'"';	$liste = requette ($sql);	return (empty ($liste) ? false : $liste[0]);}function existeEnseignant ($nom, $pass){	$sql = 'SELECT * FROM enseignant WHERE identifiant = "'.$nom.'" AND pass = "'.$pass.'"';	$liste = requette ($sql);	return (empty ($liste) ? false : $liste[0]);}function existeAdministrateur ($nom, $pass){	$sql = 'SELECT * FROM administrateur WHERE identifiant = "'.$nom.'" AND pass = "'.$pass.'"';	$liste = requette ($sql);	return (empty ($liste) ? false : $liste[0]);}?>