<?phpdefined('PAS_D_ACCES_DIRECT') or die('<h1>Acc&egrave;s Restreint</h1>');function requette ($sql){	$liste = array ();	$reponse = mysql_query($sql);	if ($reponse)	{		while ($donnee = mysql_fetch_object($reponse) )		{			array_push ($liste, $donnee);		}	}		return $liste;}function modifier ($sql){	$ok = false;	if (mysql_query($sql))	{		$ok = true;	}	return $ok;}function modeleInfosEnseignant ($id){	$sql = 'SELECT * FROM enseignant WHERE id = "'.$id.'"';	$liste = requette ($sql);	return (empty ($liste) ? false : $liste[0]);}//// Gestion des notes//function modeleListeNotes ($id){	$sql = "			SELECT *, note.id as id_note			FROM note, etudiant			WHERE note.id_enseignant = '$id'			  AND note.id_etudiant = etudiant.id			ORDER BY etudiant.nom, note.type, etudiant.filiere			";	return requette ($sql);}function modeleListeEtudiants (){	$sql = '		SELECT *		FROM etudiant		ORDER BY nom, filiere';	return requette ($sql);}function modeleChercherNote ($idn, $id){	$sql = "		SELECT *		FROM note		WHERE id = '$idn'		  AND id_enseignant = '$id'		";	$liste = requette ($sql);	return (empty ($liste) ? false : $liste[0]);}function modeleSupprimerNote ($idn, $id){	$sql = "		DELETE FROM note		WHERE id = '$idn'		  AND id_enseignant = '$id'		";	return modifier ($sql);}function modeleAjouterNote ($valeur, $type, $id_enseignant, $id_etudiant){	$sql = "		INSERT INTO note (valeur, type, id_enseignant, id_etudiant)		VALUES ('$valeur','$type','$id_enseignant','$id_etudiant')		";	return modifier ($sql);}?>