<?phpdefined('PAS_D_ACCES_DIRECT') or die('<h1>Acc&egrave;s Restreint</h1>');function requette ($sql){	$liste = array ();	$reponse = mysql_query($sql);	if ($reponse)	{		while ($donnee = mysql_fetch_object($reponse) )		{			array_push ($liste, $donnee);		}	}		return $liste;}function modeleInfosEtudiant ($id){	$sql = 'SELECT * FROM etudiant WHERE id = "'.$id.'"';	$liste = requette ($sql);	return (empty ($liste) ? false : $liste[0]);}function modeleListeNotes ($id){	$sql = "			SELECT *			FROM note, enseignant			WHERE note.id_enseignant = enseignant.id			  AND note.id_etudiant = '$id'			ORDER BY note.type, enseignant.matiere			";	return requette ($sql);}?>